#include <stdio.h>
#define MAXLINE 1000 /* maximum input line length */
#define LONGLINE 80

int super_getline(char line[], int maxline);
void copy(char to[], char from[]);

// 
// 
// 
// 11111111112222222222333333333344444444445555555555666666666677777777778888888888

/* print the longest input line */
int main()
{
  int len;                // current line length
  char line[MAXLINE];     // current input line

  while((len = super_getline(line, MAXLINE)) > 0) {
    if(len > LONGLINE) {
      printf("%d\t%s\n", len, line);
    }
  }

  return 0;
}

// super_getline: read a line into s, return length
int super_getline(char s[], int lim)
{
  int c, i;
  
  i = 0;
  while((c = getchar()) != EOF && c != '\n') {
    if(i < 1000) s[i] = c;
    i++;
  }

  if(c == '\n') {
    s[i] = c;
    i++;
  }

  s[i] = '\0';
  return i;
}

