#include <stdio.h>
#define MAXLINE 1000 /* maximum input line length */

int super_getline(char line[], int maxline);
void reverse(char line[], int len);

// 
// 
// 

/* print the longest input line */
int main()
{
  int len;                // current line length
  char line[MAXLINE];     // current input line

  while((len = super_getline(line, MAXLINE)) > 0) {
    reverse(line, len);
    printf("%s", line);
  }

  return 0;
}

// super_getline: read a line into s, return length
int super_getline(char s[], int lim)
{
  int c, i;
  
  for(i = 0; i < lim - 1 && (c = getchar()) != EOF && c != '\n'; i++) {
    s[i] = c;
  }

  if(c == '\n') {
    s[i] = c;
    i++;
  }

  s[i] = '\0';
  return i;
}

// copy: copy 'from' into 'to'; assume to is big enough
void reverse(char input[], int len)
{
  for(int i = 0; i < len / 2; i++) {
    int pos = len - 1 - i;
    int swap = input[pos];
    input[pos] = input[i];
    input[i] = swap;
  }
}

